import React, { useState } from 'react';
import { useParams } from 'react-router-dom';

const UpdateRestaurant = (props) => {

    const {id} = useParams();

    const [name, setName] = useState("");
    const [location, setLocation] = useState("");
    const [priceRange, setPriceRange] = useState("Price Range");

    return (
        <div>
            <form action="">
                <div className="form-group">
                    <label htmlFor="name">Name</label>
                    <input id="name" value={name} className="form-control" type="text"/>
                </div>

                <div className="form-group">
                    <label htmlFor="location">Location</label>
                    <input id="location" value={location} className="form-control" type="text"/>
                </div>

                <div className="form-group">
                    <label htmlFor="price_range">Price Range</label>
                    {/* <input id="price_range" className="form-control" type="number"/> */}
                    <select id="price_" value={priceRange} className="custom-select my-1 mr-sm-2">
                        <option disabled>Price Range</option>
                        <option value="1">₹</option>
                        <option value="2">₹₹</option>
                        <option value="3">₹₹₹</option>
                        <option value="4">₹₹₹₹</option>
                        <option value="5">₹₹₹₹₹</option>
                    </select>
                </div>

                <button className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}

export default UpdateRestaurant;
